import { useReducer } from "react";
import { Map } from "immutable";
import Axios from "axios";

// global state container (alternatives to Redux)

const initialState = Map({
  loading: false,
  id: undefined, // 임시. 원래는 undefined
  error: "",
  page: "login",
  region: undefined, // 임시. 원래는 undefined
  personality: undefined,
  feed: [],
  likes: [],
  favorites: []
});

const update_feed = async state => {
  // 1. 전체 (내 퍼소널리티에 맞는) 아이템들
  const feed = await Axios.post(ROOT_URL + "/?", { personal: state.personality || "EXTREME_PERSONAL" });
  // 2. 내 즐겨찾기 아이템들
  const favorites = await Axios.post(ROOT_URL + "/?", { userIdx: state.userIdx });
  // 3. 내 좋아요 아이템들
  const likes = await Axios.post(ROOT_URL + "/?", { userIdx: state.userIdx });
  return state
    .set("likes", likes)
    .set("favorites", favorites)
    .set("feed", feed);
};

const reducer = (state, action) => {
  console.log(state, action);
  switch (action.type) {
    case "LOGIN_TRY":
      return state
        .set("loading", true)
        .set("id", undefined)
        .set("error", "로딩중...");
    case "LOGIN_SUCCESS":
      return state
        .set("loading", false)
        .set("id", action.payload)
        .set("error", undefined);
    case "LOGIN_FAIL":
      return state.set("loading", false).set("error", "로그인에 실패했습니다.");
    case "REGISTER_SUCCESS":
      return state
        .set("loading", false)
        .set("page", "login")
        .set("error", "회원가입에 성공했습니다. 로그인 해 주세요.");
    case "REGISTER_FAIL":
      return state.set("loading", false).set("error", action.payload);

    case "UPDATE_REGION":
      return state.set("region", action.payload);
    case "UPDATE_PERSONALITY":
      return state.set("personality", action.payload);
    case "UPDATE_FEED":
      return update_feed(state);

    case "CLEAR_REGION":
      return state.set("region", undefined);
    case "CLEAR_PERSONALITY":
      return state.set("personality", undefined);

    case "GOTO_REGISTER":
      return state.set("page", "register");
    case "GOTO_LOGIN":
      return state.set("page", "login");
    case "CLAER_ERROR":
      return state.set("error", undefined);
    default:
      return state;
  }
};

const useGlobalReducer = () => {
  return useReducer(reducer, initialState);
};

export default useGlobalReducer;
