import React, { useContext } from "react";
import { DispatchContext } from "../App";
import useInputState from "../hooks/useInputState";
import { axios } from "../customAxios";

const Register = () => {
  const [id, idBind] = useInputState("");
  const [password, passwordBind] = useInputState("");
  const [passwordConfirm, passwordConfirmBind] = useInputState("");
  const [nickname, nicknameBind] = useInputState("");

  const dispatch = useContext(DispatchContext);

  const onSubmitHandler = e => {
    e.preventDefault();
    if (!!id || !!password || !!passwordConfirm || !!nickname) return alert("입력하지 않은 항목이 있습니다");
    if (password !== passwordConfirm) return alert("비밀번호와 비밀번호 확인이 일치하지 않습니다");
  };

  const onClickHandler = e => {
    e.preventDefault();
    console.log("?");

    dispatch("GOTO_LOGIN");
  };

  return (
    <div>
      <h1>회원가입화면 (Register) </h1>

      <form onSubmit={onSubmitHandler}>
        <div>
          <label htmlFor="id">id</label>
          <input type="text" {...idBind} />
        </div>
        <div>
          <label htmlFor="password">password</label>
          <input type="text" {...passwordBind} />
        </div>
        <div>
          <label htmlFor="passwordConfirm">passwordConfirm</label>
          <input type="text" {...passwordConfirmBind} />
        </div>
        <div>
          <label htmlFor="nickname">nickname</label>
          <input type="text" {...nicknameBind} />
        </div>
        {/* <div>
          <label htmlFor=""></label>
          <input type="text" />
        </div>
        <div>
          <label htmlFor=""></label>
          <input type="text" />
        </div>
        <div>
          <label htmlFor=""></label>
          <input type="text" />
        </div>
        <div>
          <label htmlFor=""></label>
          <input type="text" />
        </div>
        <div>
          <label htmlFor=""></label>
          <input type="text" />
        </div>
        <div>
          <label htmlFor=""></label>
          <input type="text" />
        </div> */}
        <button>회원가입</button>
      </form>
      <button onClick={onClickHandler}>로그인 화면으로 돌아가기</button>
    </div>
  );
};

export default Register;
