import Axios from "axios";

const getUrl = subject => {
  const rootUrl = "https://jsonplaceholder.typicode.com";
  switch (subject) {
    case "LOGIN":
      return rootUrl + "/user/";
    default:
      return rootUrl;
  }
};

const login = (dispatch, data) => {
  dispatch({ type: "LOGIN_TRY" });
  // Axios.post(getUrl("LOGIN"), data)
  //   .then(response => dispatch({ type: "LOGIN_SUCCESS", payload: response.id }))
  //   .catch(error => dispatch({ type: "LOGIN_FAIL", payload: "로그인 실패" }));

  // Below: TEST
  dispatch({ type: "LOGIN_SUCCESS", payload: 5 });
};

const axios = (action, dispatch, data) => {
  switch (action) {
    case "login":
      return login(dispatch, data);

    default:
      return;
  }
};

export { axios };
