import Axios from "axios";

const getUrl = subject => {
  const rootUrl = "https://jsonplaceholder.typicode.com";
  switch (subject) {
    case "LOGIN":
      return rootUrl + "/user/";
    case "REGISTER":
      return rootUrl + "/users/signup/";
    case "IS_DUPLICATED":
      return rootUrl + "/users/signup/validateEmail?email=";
    default:
      return rootUrl;
  }
};

const login = (dispatch, data) => {
  dispatch({ type: "LOGIN_TRY" });
  // Axios.post(getUrl("LOGIN"), data)
  //   .then(response => dispatch({ type: "LOGIN_SUCCESS", payload: response.id }))
  //   .catch(error => dispatch({ type: "LOGIN_FAIL", payload: "로그인 실패" }));

  // Below: TEST
  dispatch({ type: "LOGIN_SUCCESS", payload: 5 });
};

const register = async (dispatch, data) => {
  dispatch({ type: "LOGIN_TRY" });
  // dispatch({ type: "REGISTER_FAIL", payload: "중복된 이메일입니다" });
  // const isDuplicated = await Axios.get(getUrl("IS_DUPLICATED") + data.email).then(({ data }) => {
  //   if (data.status === 204) {
  //     dispatch({ type: "REGISTER_FAIL", payload: "중복된 이메일입니다" });
  //     return true;
  //   }
  //   return false;
  // });

  // if (isDuplicated)
  return;

  Axios.post(getUrl("REGISTER"), data)
    .then(response => dispatch({ type: "REGISTER_SUCCESS", payload: response.id }))
    .catch(_ => dispatch({ type: "REGISTER_FAIL", payload: "회원가입 실패" }));

  // Below: TEST
  dispatch({ type: "LOGIN_SUCCESS", payload: 5 });
};

const axios = (action, dispatch, data) => {
  switch (action) {
    case "login":
      return login(dispatch, data);
    case "register":
      return register(dispatch, data);
    default:
      return;
  }
};

export { axios };
