import React, { useState } from "react";

const SelectPersonality = () => {
  const [personality, setPersonality] = useState("NATURE_PERSONALITY");
  const onSubmitHandler = e => {
    e.preventDefault();
    if (!personality) return alert("입력하지 않은 항목이 있습니다");
  };

  const onChangeHandler = e => {
    e.preventDefault();
    setPersonality(e.target.value);
  };

  return (
    <div>
      <form onSubmit={onSubmitHandler}>
        <select name="personality" id="personality" value={personality} onChange={onChangeHandler}>
          <option value="NATURE_PERSONALITY">NATURE_PERSONALITY</option>
          <option value="extreme_PERSONALITY">EXTREME_PERSONALITY</option>
          <option value="CULTURE_PERSONALITY">CULTURE_PERSONALITY</option>
          <option value="FAMILY_PERSONALITY">FAMILY_PERSONALITY</option>
        </select>
        <button>퍼소널리티 등록</button>
      </form>
    </div>
  );
};

export default SelectPersonality;
