import React from "react";
import useInputState from "../hooks/useInputState";

const RegionSelectForm = () => {
  const [oneday, onedayBind] = useInputState("");
  const [month, monthBind] = useInputState("");
  const [purpose, purposeBind] = useInputState(1);
  const [has_accompany, has_accompanyBind] = useInputState(""); // TODO: 이 부분 문서에 안적혀있음. 미성님께 확인하고 정정.
  const [accompany_num, accompany_numBind] = useInputState("");
  const [accompany_relation, accompany_relationBind] = useInputState("");
  const [pay, payBind] = useInputState("");
  const [activity, activityBind] = useInputState("");
  const [sex, sexBind] = useInputState("");
  const [age, ageBind] = useInputState("");
  const [marriage, marriageBind] = useInputState("");

  const onSubmitHandler = e => {
    e.preventDefault();
  };
  return (
    <div>
      <form onSubmit={onSubmitHandler}>
        <div>
          <label htmlFor="oneday"></label>당일(1)/숙박(2)
          <input type="text" {...onedayBind} />
        </div>
        <div>
          <label htmlFor="month"></label>여행출발 월(1~12)
          <input type="text" {...monthBind} />
        </div>
        {/* <div>
          <label htmlFor="purpose"></label>purpose
          <input type="text" {...purposeBind} />
        </div> */}
        <div>
          <label htmlFor="has_accompany"></label>동행자 유무 (있으면 1, 없으면 2)
          <input type="text" {...has_accompanyBind} />
        </div>
        <div>
          <label htmlFor="accompany_num"></label>동행자 수(자신 제외)
          <input type="text" {...accompany_numBind} />
        </div>
        <div>
          <label htmlFor="accompany_relation"></label>동행자와의 관계(복수선택 가능)
          {/* <input type="text" {...accompany_relationBind} /> */}
          <select name="accompany_relation" id="accompany_relation" multiple>
            <option value="1"></option>
            <option value="2"></option>
            <option value="3"></option>
            <option value="4"></option>
            <option value="5"></option>
            <option value="6"></option>
          </select>
        </div>
        <div>
          <label htmlFor="pay"></label>pay
          <input type="text" {...payBind} />
        </div>
        <div>
          <label htmlFor="activity"></label>activity
          <input type="text" {...activityBind} />
        </div>
        <div>
          <label htmlFor="sex"></label>sex
          <input type="text" {...sexBind} />
        </div>
        <div>
          <label htmlFor="age"></label>age
          <input type="text" {...ageBind} />
        </div>
        <div>
          <label htmlFor="marriage"></label>marriage
          <input type="text" {...marriageBind} />
        </div>
      </form>
    </div>
  );
};

export default RegionSelectForm;
