    <div>
      <button onClick={_ => setOption(1)}>직접입력</button>
      <button onClick={_ => setOption(2)}>추천받기</button>
    </div>
  );
  return <div>{option === 0 ? optionButtons() : null}</div>;
};

export default SelectRegion;
