import React, { useContext } from "react";
import { DispatchContext, StateContext } from "../App";
import styled from "styled-components";

const FeedItems = () => {
  // const [page, setPage] = useState(1);
  // const [like, setLike] = useState([]);
  // const [dislike, setDislike] = useState([]);

  const dispatch = useContext(DispatchContext);
  const state = useContext(StateContext);

  const toggleFavorite = (e, targetId) => {
    e.preventDefault();
    let favorites = state.get("favorites");
    const idx = favorites.indexOf(targetId);
    if (idx > -1) {
      favorites.splice(idx, 1);
    } else {
      favorites.push(targetId);
    }
    dispatch({ type: "UPDATE_FAVORITES", payload: favorites });
    // TODO: axios로 서버에 전송. 나중에 즐겨찾기 페이지 볼 때는 서버에서 가져와서 확인
  };

  const toggleLike = (e, targetId) => {
    e.preventDefault();
    let likes = state.get("likes");
    const idx = likes.indexOf(targetId);
    if (idx > -1) {
      likes.splice(idx, 1);
      e.target.classList.remove("like-on");
    } else {
      likes.push(targetId);
      e.target.classList.add("like-on");
    }
    dispatch({ type: "UPDATE_LIKES", payload: likes });
  };

  const items = () => {
    return dummyItems.map((item, index) => (
      // TODO: 렌더링 때부터 (state를 따로 가지고 있길 바라는) 좋아요 데이터와 매칭시켜, like 또는 dislike 표시
      <FlexChild style={{ backgroundImage: `url(${item.imageUrl})` }} key={items.title + String(index)} id={items.title + String(index)}>
        <ToggleFavoriteButton onClick={e => toggleFavorite(e, item.number)} /> {/* TODO: 어떤 값으로 즐겨찾기를 보관하는지 확인 */}
        <ToggleLikeButton onClick={e => toggleLike(e, item.number)} /> {/* TODO: 어떤 값으로 즐겨찾기를 보관하는지 확인 */}
      </FlexChild>
    ));
  };

  return (
    <>
      <div>현재 선택된 지역: {state.get("region")}</div>
      <div>현재 선택된 퍼소널리티 : {state.get("personality")}</div>
      <FlexParent>{items()}</FlexParent>
      {/* <button onClick={_ => (page > 1 ? setPage(prev => prev - 1) : null)}>Prev page</button> */}
      {/* <button onClick={_ => setPage(prev => prev + 1)}>Next page</button> */}
    </>
  );
};

const FlexParent = styled.div`
  display: flex;
  justify-content: space-around;
  flex-wrap: wrap;
  margin: 0 auto;
`;
const FlexChild = styled.div`
  position: relative;
  width: 100px;
  height: 100px;
  margin: 10px;
  border: 1px red solid;
  text-align: center;
  background-size: cover;
`;
const ToggleFavoriteButton = styled.div`
  position:absolute;
  height:20px;
  width:20px;
  border:1px red solid;
  background-color:yellow
  top:10px;
  left:10px;
`;
const ToggleLikeButton = styled.div`
  position:absolute;
  height:20px;
  width:20px;
  border:1px red solid;
  background-color:blue
  bottom:10px;
  left:10px
  &.like-on{
    background-color:green
  }
`;
export default FeedItems;
