import Axios from "axios";

const getUrl = subject => {
  const rootUrl = "https://jsonplaceholder.typicode.com";
  switch (subject) {
    case "LOGIN":
      return rootUrl + "/user/";
    default:
      return rootUrl;
  }
};

const login = (dispatch, payload) => {
  dispatch({ type: "LOGIN_TRY" });
  Axios.post(getUrl("LOGIN"), payload)
    .then(response => dispatch({ type: "LOGIN_SUCCESS", paylaod: response.id }))
    .catch(error => dispatch({ type: "LOGIN_FAIL", payload: error }));
};

const axios = (action, dispatch, payload) => {
  switch (action) {
    case "login":
      login(dispatch, payload);
      break;

    default:
      return;
  }
};

export { axios };
