import React from "react";
import Home from "./pages/Home";
import Error from "./pages/Error";
import Login from "./pages/Login";

import useGlobalReducer from "./hooks/useGlobalReduceer";

export const GlobalContext = React.createContext();

function App() {
  const [state, reducer] = useGlobalReducer();

  return (
    <GlobalContext.Provider context={(state, reducer)}>
      <div className="App">
        {state.get("error") ? <Error /> : null}
        {state.get("id") ? <Home /> : <Login />}
      </div>
    </GlobalContext.Provider>
  );
}

export default App;
