import React, { useContext } from "react";
import { DispatchContext } from "../App";
import useInputState from "../hooks/useInputState";

const Home = () => {
  const [id, idBind] = useInputState("");
  const [password, passwordBind] = useInputState("");
  const [passwordConfirm, passwordConfirmBind] = useInputState("");
  const [nickname, nicknameBind] = useInputState("");

  const dispatch = useContext(DispatchContext);

  const onSubmitHandler = e => {
    e.preventDefault();
    if (password !== passwordConfirm) return alert("비밀번호가 일치하지 않습니다");
    if (!!id || !!password || !!passwordConfirm || !!nickname) return alert("누락된 회원정보가 있습니다");
  };

  return (
    <div>
      <h1>메인화면 (Home) </h1>
